func InterfaceToServiceGroupSlice(data interface{}) []*ServiceGroup {
	list := data.([]interface{})
	result := MakeServiceGroupSlice()
	for _, item := range list {
		result = append(result, InterfaceToServiceGroup(item))
	}
	return result
}

func MakeServiceGroupSlice() []*ServiceGroup {
	return []*ServiceGroup{}
}
