package models

// Tag

import "encoding/json"

type Tag struct {
	DisplayName string         `json:"display_name"`
	UUID        string         `json:"uuid"`
	FQName      []string       `json:"fq_name"`
	TagID       U32BitHexInt   `json:"tag_id"`
	TagValue    string         `json:"tag_value"`
	IDPerms     *IdPermsType   `json:"id_perms"`
	Annotations *KeyValuePairs `json:"annotations"`
	Perms2      *PermType2     `json:"perms2"`
	TagTypeName string         `json:"tag_type_name"`

	// tag_type <utils.Reference Value>
	TagTypeRefs []*TagTagTypeRef

	Projects    []*TagProject
	ConfigRoots []*TagConfigRoot
}

// <utils.Reference Value>
type TagTagTypeRef struct {
	UUID string   `json:"uuid"`
	To   []string `json:"to"` //FQDN

}

type TagProject struct {
	UUID string   `json:"uuid"`
	To   []string `json:"to"` //FQDN

}

type TagConfigRoot struct {
	UUID string   `json:"uuid"`
	To   []string `json:"to"` //FQDN

}

func (model *Tag) String() string {
	b, _ := json.Marshal(model)
	return string(b)
}

func MakeTag() *Tag {
	return &Tag{
		//TODO(nati): Apply default
		TagValue:    "",
		IDPerms:     MakeIdPermsType(),
		Annotations: MakeKeyValuePairs(),
		Perms2:      MakePermType2(),
		TagTypeName: "",
		TagID:       MakeU32BitHexInt(),
		UUID:        "",
		FQName:      []string{},
		DisplayName: "",
	}
}

