func InterfaceToTagSlice(data interface{}) []*Tag {
	list := data.([]interface{})
	result := MakeTagSlice()
	for _, item := range list {
		result = append(result, InterfaceToTag(item))
	}
	return result
}

func MakeTagSlice() []*Tag {
	return []*Tag{}
}
